import React, { useRef, useState } from 'react';
import emailjs from '@emailjs/browser';
import { FiMail, FiMapPin, FiPhone, FiSend } from 'react-icons/fi';
import './Contact.css';

const Contact = () => {
  const form = useRef();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState(null);

  const contactInfo = [
    {
      icon: <FiMail />,
      title: 'Email',
      value: 'upfrontretaile@gmail.com',
      link: 'mailto:upfrontretaile@gmail.com',
    },
    {
      icon: <FiPhone />,
      title: 'Phone',
      value: '+1 (555) 123-4567',
      link: 'tel:+15551234567',
    },
    {
      icon: <FiMapPin />,
      title: 'Location',
      value: 'Nairobi, Kenya',
    },
  ];

  const sendEmail = (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    console.log("Sending email with params:", {
      serviceId: process.env.REACT_APP_EMAILJS_SERVICE_ID,
      templateId: process.env.REACT_APP_EMAILJS_TEMPLATE_ID,
      publicKey: process.env.REACT_APP_EMAILJS_PUBLIC_KEY ? "Present" : "Missing"
    });

    emailjs.sendForm(
      process.env.REACT_APP_EMAILJS_SERVICE_ID,
      process.env.REACT_APP_EMAILJS_TEMPLATE_ID,
      form.current,
      process.env.REACT_APP_EMAILJS_PUBLIC_KEY
    )
    .then((result) => {
      console.log('✅ Email sent successfully!', result);
      console.log('Status:', result.status);
      console.log('Text:', result.text);
      setSubmitStatus('success');
      form.current.reset();
      setTimeout(() => setSubmitStatus(null), 5000);
    })
    .catch((error) => {
      console.error('❌ Email failed to send:', error);
      
      // Log detailed error information
      if (error.text) {
        console.error('Error text:', error.text);
      }
      if (error.status) {
        console.error('Error status:', error.status);
      }
      
      // Show more specific error message
      if (error.text === 'Invalid public key') {
        setSubmitStatus('error-public-key');
      } else if (error.text?.includes('service')) {
        setSubmitStatus('error-service');
      } else if (error.text?.includes('template')) {
        setSubmitStatus('error-template');
      } else {
        setSubmitStatus('error');
      }
      
      setTimeout(() => setSubmitStatus(null), 5000);
    })
    .finally(() => {
      setIsSubmitting(false);
    });
  };

  return (
    <section id="contact" className="contact">
      <div className="container">
        <div className="contact-header">
          <h2 className="section-title">Get In Touch</h2>
          <p className="section-subtitle">
            Have a project in mind? Let's discuss how we can work together to bring your ideas to life.
          </p>
        </div>

        <div className="contact-content">
          <div className="contact-info fade-in">
            <h3>Let's Connect</h3>
            <p>
              I'm always open to discussing new opportunities, whether it's a 
              freelance project, full-time role, or just chatting about tech.
            </p>
            
            <div className="contact-details">
              {contactInfo.map((info) => (
                <div key={info.title} className="contact-detail">
                  <div className="contact-icon">{info.icon}</div>
                  <div>
                    <h4>{info.title}</h4>
                    {info.link ? (
                      <a href={info.link} className="contact-link">
                        {info.value}
                      </a>
                    ) : (
                      <p>{info.value}</p>
                    )}
                  </div>
                </div>
              ))}
            </div>

            <div className="contact-hours">
              <h4>Response Time</h4>
              <p>I typically respond within 24 hours on weekdays.</p>
            </div>
          </div>

          <div className="contact-form-wrapper fade-in">
            <form ref={form} onSubmit={sendEmail} className="contact-form">
              <div className="form-group">
                <input
                  type="text"
                  name="name"
                  placeholder="Your Name"
                  required
                  className="form-input"
                />
              </div>

              <div className="form-group">
                <input
                  type="email"
                  name="email"
                  placeholder="Your Email"
                  required
                  className="form-input"
                />
              </div>

              <div className="form-group">
                <input
                  type="text"
                  name="subject"
                  placeholder="Subject"
                  required
                  className="form-input"
                />
              </div>

              <div className="form-group">
                <textarea
                  name="message"
                  placeholder="Your Message"
                  rows="6"
                  required
                  className="form-input"
                ></textarea>
              </div>

              <button
                type="submit"
                className="btn btn-submit"
                disabled={isSubmitting}
              >
                {isSubmitting ? 'Sending...' : 'Send Message'}
                {!isSubmitting && <FiSend className="send-icon" />}
              </button>

              {/* Success Message */}
              {submitStatus === 'success' && (
                <div className="submit-message success">
                  ✓ Message sent successfully! I'll get back to you soon.
                </div>
              )}

              {/* Generic Error Message */}
              {submitStatus === 'error' && (
                <div className="submit-message error">
                  ✗ Something went wrong. Please try again or email me directly.
                </div>
              )}

              {/* Specific Error: Invalid Public Key */}
              {submitStatus === 'error-public-key' && (
                <div className="submit-message error">
                  ✗ Invalid public key. Please check your EmailJS configuration.
                </div>
              )}

              {/* Specific Error: Service ID Issue */}
              {submitStatus === 'error-service' && (
                <div className="submit-message error">
                  ✗ Service ID error. Please verify your EmailJS service.
                </div>
              )}

              {/* Specific Error: Template ID Issue */}
              {submitStatus === 'error-template' && (
                <div className="submit-message error">
                  ✗ Template ID error. Please verify your EmailJS template.
                </div>
              )}
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
